import express from 'express';
import bodyParser from 'body-parser';
import swaggerUI from 'swagger-ui-express';
import swaggerJSDoc from 'swagger-jsdoc';
import userRoutes from './routes/userRoute';
import dbConnect from './db/mongoose';
import articleRoutes from './routes/articleRoute';
import categoryRoutes from './routes/categoryRoute';

const app = express();

app.use(bodyParser.json());
app.use(bodyParser.urlencoded({ extended: false }));

dbConnect();

const swaggerOptions = {
  swaggerDefinition: {
    info: {
      title: 'My-brand API',
      version: '1.0.0',
      description: 'Personal website with blog',
      contact: {
        name: 'Celestin Niyindagiriye(Developer)',
      },
      servers: ['https://celestin-brand.herokuapp.com/'],
    },
  },
  apis: ['server/index.js', 'server/routes/*.js'],
};

const swaggerDocs = swaggerJSDoc(swaggerOptions);
app.use('/my-brand-api-docs', swaggerUI.serve, swaggerUI.setup(swaggerDocs));

/**
 * @swagger
 * /home:
 *  get:
 *    summary: Welcome to this site
 *    description: <h1>Read to win. Time flies but you are the pilot</h1>
 *    responses:
 *      '200':
 *        description: success
 */
app.get('/', (req, res) => {
  res.status(200)
    .send(
      `
        <h1>Welcome my friend </h1>
        <p>This is an API of for my website
        <a href='https://celestin-brand.herokuapp.com/my-brand-api-docs'><b>Explore API<b></a>
      `,
    );
});

app.use('/users', userRoutes);
app.use('/articles', articleRoutes);
app.use('/article-categories', categoryRoutes);

app.listen(process.env.PORT || 3000, () => {
  // eslint-disable-next-line no-console
  console.log('Server is running...');
});

export default app;

import express from 'express';
import {
  userSignup, userLogin, getAllUsers, getUser, deleteUser,
} from '../controllers/userController';

const userRoutes = express.Router();

userRoutes.post('/signup', userSignup);

userRoutes.post('/login', userLogin);

/**
 * @swagger
 * /users:
 *  get:
 *    summary: Returns a list of users
 *    description: <p>You should get all <b>Users</b> have been registered to this site</p>
 *
 *    responses:
 *      '200':
 *        description: <h1>Gets list of users</h1>
 */
userRoutes.get('/', getAllUsers);

/**
 * @swagger
 * /users/{userId}:
 *  get:
 *    summary: Get user by ID
 *    parameters:
 *      - in: path
 *        name: userId
 *        schema:
 *          type: string
 *        required: true
 *        description: ID of the user to get
 *    responses:
 *      '200':
 *        description: success
 */
userRoutes.get('/:userId', getUser);
userRoutes.delete('/', deleteUser);

export default userRoutes;

/* eslint-disable no-underscore-dangle */
import bcrypt from 'bcrypt';
import jsonwebtoken from 'jsonwebtoken';
import dotenv from 'dotenv';
import User from '../models/userModel';

dotenv.config();

export function userSignup(req, res) {
  User.find({ email: req.body.email })
    .exec((err, user) => {
      if (err || user.length >= 1) return res.status(404).json({ message: 'Email already exists' });
      return bcrypt.hash(req.body.password, 10, (hashError, hash) => {
        if (hashError || !hash) return res.status(500).json({ message: 'Something is wrong' });
        const newUser = new User({
          email: req.body.email,
          password: hash,
          userType: req.body.userType,
        });
        return newUser.save((error, createdUser) => {
          if (error || !createdUser) return res.status(404).json({ message: 'You can\'t create an account' });
          return res.status(201).json({
            message: 'User created',
            id: createdUser._id,
            email: createdUser.email,
            password: createdUser.password,
          });
        });
      });
    });
}

export function getAllUsers(req, res) {
  User.find()
    .exec((err, docs) => {
      if (err || !docs[0]) return res.status({ message: 'No user added yet' });
      return res.status(200).json({
        countUsers: docs.length,
        users: docs.map((doc) => ({
          id: doc._id,
          email: doc.email,
          userType: doc.userType,
        })),
      });
    });
}

export function getUser(req, res) {
  const id = req.params.userId;
  User.findById({ _id: id })
    .exec((err, user) => {
      if (err || !user) return res.status(404).json({ message: 'User not found or something is wrong' });
      return res.status(200).json({
        id: user._id,
        email: user.email,
        password: user.password,
        userType: user.userType,
      });
    });
}

export function deleteUser(req, res) {
  const id = req.body.userId;
  User.deleteOne({ _id: id }, (err, deletedUser) => {
    if (err || deletedUser.deletedCount !== 1) return res.status(404).json({ message: 'Deletion failed' });
    return res.status(200).json({
      message: 'User has been deleted',
    });
  });
}

export function userLogin(req, res) {
  User.find({ email: req.body.email })
    .exec((err, users) => {
      if (err || users.length < 1) return res.status(404).json({ message: 'Login failed' });
      return bcrypt.compare(req.body.password, users[0].password, (error, result) => {
        if (error || !result) return res.status(404).json({ message: 'Something is wrong' });
        const token = jsonwebtoken.sign({
          email: users[0].email,
          userId: users[0]._id,
        },
        process.env.JWT_TOKEN,
        {
          expiresIn: '1h',
        });
        return res.status(200).json({
          message: 'Login successful',
          token,
        });
      });
    });
}

import { Schema, model } from 'mongoose';

const userSchema = Schema({
  email: {
    type: String,
    required: true,
    unique: true,
    match: /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?(?:\.[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?)*$/,
  },
  password: { type: String, required: true },
  userType: { type: String, default: 'client' },
});

export default model('User', userSchema);

import mongoose from 'mongoose';
import dotenv from 'dotenv';

dotenv.config();

mongoose.set('useUnifiedTopology', true);
mongoose.set('useCreateIndex', true);
mongoose.Promise = global.Promise;

export default function dbConnect() {
  mongoose.connect(process.env.MONGODB_URI, { useNewUrlParser: true, useFindAndModify: false });
}

/* eslint-disable no-underscore-dangle */
import Category from '../models/categoryModel';
import User from '../models/userModel';
import Article from '../models/articleModel';

export function addArticle(req, res) {
  return User.findById({ _id: req.body.userId }, (err, userFound) => {
    if (err || userFound.userType !== 'admin') return res.status(404).json({ message: 'Author not found or something is wrong' });
    return Category.findById({ _id: req.body.categoryId }, (error, categoryFound) => {
      if (error || !categoryFound) return res.status(404).json({ message: 'Category not found or something is wrong' });
      const article = new Article({
        title: req.body.title,
        visible: req.body.visible,
        content: req.body.content,
        articleImage: req.body.articleImage,
        category: req.body.categoryId,
        user: req.body.userId,
      });
      return article.save((articleError, addedArticle) => {
        if (articleError || !addedArticle) return res.status(404).json({ message: 'Something is wrong' });
        return res.status(201).json({
          message: 'Article created successfully',
          createdArticle: {
            id: addedArticle._id,
            author: addedArticle.user,
            topic: addedArticle.category,
            title: addedArticle.title,
            visible: addedArticle.visible,
            content: addedArticle.content,
            articleImage: addedArticle.articleImage,
          },
          request: {
            type: 'GET',
            url: `http//localhost:3000/article/${addedArticle._id}`,
          },
        });
      });
    });
  });
}

export function getAllArticles(req, res) {
  Article.find()
    .select('title content visible category user')
    .populate('category')
    .populate('user')
    .exec((err, docs) => {
      if (err || !docs[0]) return res.status(404).json({ message: 'No article added yet or somthing is wrong!' });
      return res.status(200).json({
        articles: docs.map((doc) => ({
          id: doc._id,
          title: doc.title,
          content: doc.content,
          visible: doc.visible,
          category: doc.category.name,
          author: doc.user.email,
        })),
      });
    });
}

export function getArticle(req, res) {
  Article.findById(req.params.articleId)
    .populate('category', 'name')
    .populate('user', 'email')
    .exec((err, article) => {
      if (err || !article) return res.status(404).json({ message: 'Article not found or somethin is wrong!' });
      return res.status(200).json({
        id: article._id,
        title: article.title,
        content: article.content,
        visible: article.visible,
        category: article.category.name,
        author: article.user.email,
        request: {
          type: 'GET',
          url: 'http://localhost:3000/article',
        },
      });
    });
}

export function updateArticle(req, res) {
  const id = req.body.articleId;
  User.findById({ _id: req.body.userId }, (err, userFound) => {
    if (err || userFound.userType !== 'admin') return res.status(404).json({ message: 'Author not found or something is wrong' });
    return Category.findById({ _id: req.body.categoryId }, (error, categoryFound) => {
      if (error || !categoryFound) return res.status(404).json({ message: 'Category not found or something is wrong' });
      return Article.updateOne({ _id: id }, {
        title: req.body.title,
        visible: req.body.visible,
        content: req.body.content,
        articleImage: req.body.articleImage,
        category: req.body.categoryId,
        user: req.body.userId,
      }, { new: true }, (newError, updatedArticle) => {
        if (newError || !updatedArticle) return res.status(404).json({ message: 'Something is wrong' });
        return res.status(201).json({
          message: 'Article updated successfully',
          updatedArticle: {
            id: updatedArticle._id,
            author: updatedArticle.user,
            topic: updatedArticle.category,
            title: updatedArticle.title,
            visible: updatedArticle.visible,
            content: updatedArticle.content,
            articleImage: updatedArticle.articleImage,
          },
          request: {
            type: 'GET',
            url: `http//localhost:3000/articles/${updatedArticle._id}`,
          },
        });
      });
    });
  });
}

export function deleteArticle(req, res) {
  const id = req.body.articleId;
  Article.deleteOne({ _id: id })
    .exec((err, deletedArticle) => {
      if (err || deletedArticle.deletedCount !== 1) return res.status(404).json({ message: 'Deletion failed or somethin is wrong' });
      return res.status(201).json({
        message: 'Article deleted successfully',
        request: {
          type: 'POST',
          url: 'http://localhost:3000/articles',
          body: {
            title: 'String',
            content: 'String',
            visible: 'Boolean',
            articleImage: 'String',
            category: 'Category',
            user: 'User',
          },
        },
      });
    });
}

import { Schema, model } from 'mongoose';

const CategorySchema = Schema({
  user: { type: Schema.Types.ObjectId, ref: 'User' },
  name: { type: String },
  date: {
    type: Date,
    default: Date.now,
  },
});

export default model('Category', CategorySchema);

import { Schema, model } from 'mongoose';

const articleSchema = Schema({
  user: { type: Schema.Types.ObjectId, ref: 'User' },
  category: { type: Schema.Types.ObjectId, ref: 'Category' },
  title: { type: String, required: true },
  visible: { type: Boolean, default: false },
  articleImage: { type: String },
  content: { type: String, required: true },
  date: {
    type: Date,
    default: Date.now,
  },
});

export default model('Article', articleSchema);

/* eslint-disable no-underscore-dangle */
import Category from '../models/categoryModel';
import User from '../models/userModel';

export function getAllCategories(req, res) {
  Category.find()
    .sort('name')
    .select('_id name')
    .populate('user')
    .exec((err, selectedCategories) => {
      if (err || !selectedCategories[0]) return res.status(404).json({ message: 'Something is wrong' });
      return res.status(200).json({
        countArticleCategories: selectedCategories.length,
        articleCategories: selectedCategories.map((category) => ({
          id: category._id,
          name: category.name,
          createdBy: category.user.email,
        })),
      });
    });
}

export function addCategory(req, res) {
  User.findById({ _id: req.body.userId }, (err, user) => {
    if (err || !user) return res.status(404).json({ message: 'Something is wrong' });
    const articleCategory = new Category({
      name: req.body.name,
      user: req.body.userId,
    });
    return articleCategory.save((error, createdCategory) => {
      if (error || !createdCategory) return res.status(404).json({ message: 'Something is wrong' });
      return res.status(201).json({
        message: 'Article category has been created successfully',
        createdArticleCategory: {
          id: createdCategory._id,
          categoryName: createdCategory.name,
        },
      });
    });
  });
}

export function getCategory(req, res) {
  Category.findById({ _id: req.params.categoryId },
    (err, selectedCategory) => {
      if (err || !selectedCategory) return res.status(404).json({ message: 'Something is wrong!' });
      return res.status(200).json({
        id: selectedCategory._id,
        name: selectedCategory.name,
        createdBy: selectedCategory.user.email,
      });
    });
}

export function updateCategory(req, res) {
  const id = req.body.categoryId;
  User.findById({ _id: req.body.userId }, (err, user) => {
    if (err || !user) return res.status(404).json({ message: 'Something is wrong' });
    return Category.findOneAndUpdate({ _id: id },
      {
        name: req.body.name,
        user: req.body.userId,
      }, { new: true }, (error, updatedCategory) => {
        if (error || !updatedCategory) return res.status(404).json({ message: 'Something is wrong' });
        return res.status(201).json({
          message: 'Article Category updated successfully',
          updates: {
            id: updatedCategory.id,
            name: updatedCategory.name,
            createdBy: updatedCategory.user,
          },
        });
      });
  });
}

export function deleteCategory(req, res, done) {
  Category.deleteOne({ _id: req.body.categoryId }, (err, result) => {
    if (err) return res.status(404).json({ message: 'Something is wrong' });
    if (result.deletedCount === 1) {
      return res.status(201).json({
        message: 'Category has been deleted successfully',
      });
    }
    res.status(500).json({ message: 'Article Category not found' });
    return done(null, result);
  });
}
